import React, { useState, useEffect } from 'react';
import AnimeCard from './AnimeCard';
import './App.css';

function App() {
  const [animes, setAnimes] = useState([]);
  const [filteredAnimes, setFilteredAnimes] = useState([]);
  const [selectedAnime, setSelectedAnime] = useState(null);
  const [relatedAnimes, setRelatedAnimes] = useState([]);
  const [searchQuery, setSearchQuery] = useState('');

  useEffect(() => {
    fetch('http://127.0.0.1:8000/')
      .then(response => response.json())
      .then(data => {
        setAnimes(data);
        setFilteredAnimes(data);
      })
      .catch(error => console.error('Error fetching data:', error));
  }, []);

  useEffect(() => {
    if (searchQuery === '') {
      setFilteredAnimes(animes);
    } else {
      setFilteredAnimes(
        animes.filter(anime =>
          anime.title.toLowerCase().includes(searchQuery.toLowerCase())
        )
      );
    }
  }, [searchQuery, animes]);

  const handleAnimeClick = (anime) => {
    setSelectedAnime(anime);
    const filtered = animes.filter(a => a.clas === anime.clas && a.id !== anime.id);
    setRelatedAnimes(filtered.slice(0, 4));
  };

  const handleBackClick = () => {
    setSelectedAnime(null);
    setRelatedAnimes([]);
  };

  const handleSearchChange = (event) => {
    setSearchQuery(event.target.value);
  };

  return (
    <div className="anime-gallery">
      <input
        type="text"
        placeholder="Search by title..."
        value={searchQuery}
        onChange={handleSearchChange}
        style={{ margin: '20px', padding: '10px', width: '300px' }}
      />
      {selectedAnime ? (
        <div>
          <button onClick={handleBackClick} style={{ margin: '20px', padding: '10px' }}>
            Back to All Animes
          </button>
          <h2>{selectedAnime.title}</h2>
          <div className="related-animes">
            {relatedAnimes.map(anime => (
              <AnimeCard key={anime.id} anime={anime} onClick={handleAnimeClick} />
            ))}
          </div>
        </div>
      ) : (
        filteredAnimes.map(anime => (
          <div className="anime-card" key={anime.id}>
            <AnimeCard anime={anime} onClick={handleAnimeClick} />
          </div>
        ))
      )}
    </div>
  );
}

export default App;
